import styled from "styled-components";

export const ColumnWrapper = styled.div`
display: flex;
flex-direction: column;
color: white;
height: 100%;
min-height: 200px;
width: 100%;
justify-content: center;
align-items: center;
border-right: ${({position}) => position==='third' ? 'none': '2px solid white'}
`

export const IconHolder = styled.div`
display: flex;
color: white;
max-width: 30px;
align-items: center;
justify-content: center;
`
export const IconSubtitle=styled.p`
color: white;
display: flex;
align-items: center;
line-height: 1.5;
letter-spacing: .1px
`
