import styled from "styled-components";

export const ProjectWrapper = styled.div`
background: white;
display: flex;
width: 100%;
min-height: 400px;
height: auto;
justify-content: center;
align-items: center;
flex-direction: column;
`

export const ProjectCard = styled.div`
display: flex;
flex-direction: row;
max-width: 1300px;
min-width: 800px;
min-height: 200px;
height: auto;
justify-content: center;
align-items: center;
padding: 20px 0;
`

export const ImageWrapper = styled.div`
display: flex;
width: auto;
min-width: 30%;
padding: 5px;
border-radius: 3px;
height: auto;
min-height: 150px;
background: green;
justify-content: center;
align-items: center`

export const DescriptionWrapper = styled.div`
display: flex;
width: 100%;
padding: 5px;
height: 100%;
min-height:150px;
background: blue;
justify-content: center;
align-items: center`

export const BulletList = styled.ul`
text-align: center;
list-style-type: disc;
color: white; 
padding: 8px;
`
export const BulletPoint =styled.li`
justify-content: center;
align-items: center`
