import styled from "styled-components";

export const ProjectCard = styled.div`
display: flex;
flex-direction: row;
max-width: 1300px;
min-width: 800px;
height: auto;
justify-content: center;
align-items: center;
padding: 8px;
background: red;
border-radius: 20px;
`

export const ImageWrapper = styled.div`
display: flex;
width: auto;
min-width: 30%;
padding: 5px;
margin-right: 5px;
border-radius: 8px;
height: auto;
min-height: 150px;
background: green;
justify-content: center;
align-items: center`

export const DescriptionWrapper = styled.div`
display: flex;
width: 100%;
padding: 5px;
border-radius: 8px;
height: 100%;
min-height:150px;
background: blue;
justify-content: center;
align-items: center`

export const BulletList = styled.ul`
text-align: center;
color: white; 
padding: 8px;
list-style-type: disc;
`
export const BulletPoint =styled.li`
justify-content: center;
align-items: center;
list-style-type: disc;
color: white;
`
