import React, { useState } from "react";
import {animateScroll as scroll} from 'react-scroll'

import {
  DropdownWrapper,
  Nav,
  List,
  DropDownLink,
} from "./DropdownElements";
import { MdCloseFullscreen } from "react-icons/md";
export const DropdownNav = ({ isOpen, toggle }) => {
  const toggleHome =() => {
    scroll.scrollToTop();
    toggle
  }
  

  return (
    <DropdownWrapper isOpen={isOpen} onClick={toggle}>
      <Nav onClick={toggle}>
        <MdCloseFullscreen  onClick={toggle}/>
      </Nav>
      <List>
        <DropDownLink to="/" onClick={toggle,toggleHome}>
          Home
        </DropDownLink>
        <DropDownLink to="aboutme" 
                smooth={true} duration ={500} spy={true} offset={-80} 
                onClick={toggle}>
          About Me
        </DropDownLink>
        <DropDownLink to="education" 
                smooth={true} duration ={500} spy={true} offset={-80} 
                onClick={toggle}>
          Education
        </DropDownLink>
        <DropDownLink to="projects" 
                smooth={true} duration ={500} spy={true} offset={-80} 
                onClick={toggle}>
          Coding Projects
        </DropDownLink>
      </List>
    </DropdownWrapper>
  );
};
